import 'package:fitness/moduls/home/controller/home_controller.dart';import 'package:flutter/material.dart';import 'package:flutter_html/flutter_html.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'package:getwidget/getwidget.dart';class TableTraining extends StatefulWidget {  String? name;  TableTraining({super.key, required this.name});  @override  State<TableTraining> createState() => _TableTrainingState();}class _TableTrainingState extends State<TableTraining> {  @override  void initState() {    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        backgroundColor: Theme.of(context).primaryColor,        title: Text(          widget.name!,          style: TextStyle(              fontSize: 18.sp,              color: Colors.white,              fontWeight: FontWeight.bold),        ),        centerTitle: true,        leading: IconButton(          icon: Icon(Icons.arrow_back_ios),          onPressed: () {            Get.back();          },        ),      ),      body: GetBuilder<HomeController>(builder: (logic) {        return ListView.builder(            padding: EdgeInsets.symmetric(horizontal: 12.w, vertical: 16.h),            itemCount: logic.subActivities!.data!.length,            itemBuilder: (context, index) {              return Container(                // padding: EdgeInsets.a,                decoration: BoxDecoration(                  color: Colors.white,                  borderRadius: BorderRadius.circular(8),                  // border: Border.all(color: Colors.black),                ),                child: GFAccordion(                  expandedTitleBackgroundColor: Theme.of(context).primaryColor,                  contentBorderRadius: BorderRadius.circular(4),                  collapsedTitleBackgroundColor: Theme.of(context).primaryColor,                  titleBorderRadius: BorderRadius.circular(4),                  textStyle: TextStyle(fontSize: 18.sp, color: Colors.white),                  contentBorder:                      Border.all(color: Theme.of(context).primaryColor),                  title: logic.subActivities!.data![index].title!,                  contentChild: Html(                    data: logic.subActivities!.data![index].description!,                  ),                ),              );            });      }),    );  }}