import 'package:fitness/moduls/main/controller/main_controller.dart';import 'package:fitness/utils/component/component.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'from_signIn.dart';class LoginScreen extends StatelessWidget {  const LoginScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Scaffold(      body: SafeArea(        child: Container(          width: double.infinity,          height: double.infinity,          decoration: const BoxDecoration(              image: DecorationImage(                  fit: BoxFit.cover,                  image: AssetImage('assets/images/form1.png'))),          child: GetBuilder<MainController>(builder: (logic) {            return Form(              key: logic.formKeyLogin,              child: Padding(                padding: EdgeInsets.symmetric(vertical: 40.h, horizontal: 16.w),                child: Column(                  crossAxisAlignment: CrossAxisAlignment.start,                  children: [                    Row(                      mainAxisAlignment: MainAxisAlignment.spaceBetween,                      children: [                        Text(                          "أهلا بك...",                          style: TextStyle(                              fontSize: 28.sp,                              fontWeight: FontWeight.w600,                              color: Theme.of(context).primaryColor),                        ),                        SizedBox(                            width: 50.w,                            height: 50.h,                            child: Image.asset(                              'assets/images/logo.png',                              fit: BoxFit.fill,                            ))                      ],                    ),                    SizedBox(                      height: 15.h,                    ),                    Text(                      'تسجيل الدخول',                      style: TextStyle(                          fontSize: 20.sp, fontWeight: FontWeight.w600),                    ),                    SizedBox(                      height: 8.h,                    ),                    SizedBox(                      height: 8.h,                    ),                    Text(                      'الرقم',                      style: TextStyle(                          fontSize: 16.sp, fontWeight: FontWeight.w600),                    ),                    SizedBox(                      height: 8.h,                    ),                    SizedBox(                      height: 44.h,                      child: defaultFormField(                          borderRadius: 8,                          borderColor: Theme.of(context).primaryColor,                          controller: logic.phoneLogin,                          validate: (v) {                            if (v!.isEmpty) {                              logic.showSnackBar('الرقم');                            }                          },                          type: TextInputType.number),                    ),                    SizedBox(                      height: 18.h,                    ),                    Text(                      'كلمة المرور',                      style: TextStyle(                          fontSize: 16.sp, fontWeight: FontWeight.w600),                    ),                    SizedBox(                      height: 8.h,                    ),                    SizedBox(                      height: 44.h,                      child: defaultFormField(                          borderRadius: 8,                          borderColor: Theme.of(context).primaryColor,                          controller: logic.passwordLogin,                          validate: (v) {                            if (v!.isEmpty) {                              logic.showSnackBar('كلمة المرور ');                            }                          },                          type: TextInputType.text),                    ),                    SizedBox(                      height: 8.h,                    ),                    Row(                      mainAxisAlignment: MainAxisAlignment.start,                      crossAxisAlignment: CrossAxisAlignment.center,                      children: [                        Text(" ليس لدي حساب ؟ "),                        InkWell(                          onTap: () {                            Get.off(FormBegin());                          },                          child: Text(                            "تسجيل جديد",                            style: TextStyle(                                fontWeight: FontWeight.w600,                                color: Theme.of(context).primaryColor),                          ),                        )                      ],                    ),                    SizedBox(                      height: 18.h,                    ),                    SizedBox(                      height: 44.h,                      width: double.infinity,                      child: ElevatedButton(                        child: Text(                          "تسجيل دخول",                        ),                        onPressed: () {                          logic.login();                        },                      ),                    ),                  ],                ),              ),            );          }),        ),      ),    );  }}