import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';class ProcessLoading extends StatelessWidget {  const ProcessLoading({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      padding: const EdgeInsets.all(21),      height: 230.h,      decoration: BoxDecoration(          color: Colors.white,          border: Border.all(color: Colors.white),          borderRadius: const BorderRadius.only(              topLeft: Radius.circular(30), topRight: Radius.circular(30))),      child: Column(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          Text(            'جاري تنفيذ طلبك',            style: Theme.of(context)                .textTheme                .bodyText2!                .copyWith(fontWeight: FontWeight.w600),          ),          SizedBox(            height: 45.h,            width: 45.w,            child: CircularProgressIndicator(              backgroundColor: Theme.of(context).primaryColor,            ),          ),          GestureDetector(            onTap: () {              Get.back();            },            child: Text(              'إلغاء'.tr,              style: Theme.of(context)                  .textTheme                  .bodyText2!                  .copyWith(fontSize: 18, fontWeight: FontWeight.w600),            ),          )        ],      ),    );  }}